            <tr>
                <td>{{ promotion.promoId }}</td>
                <td>{{ promotion.promoPercent }}</td>
                <td>{{ promotion.promoLibelle }}</td>
                <td>{{ promotion.promoActive ? 'Oui' : 'Non' }}</td>
                <td>{{ promotion.promoDateFin ? promotion.promoDateFin|date('Y-m-d H:i:s') : '' }}</td>
